INSERT INTO php2.news (id, title, content) VALUES (1, 'Россию обязали заплатить Pussy Riot за дело о панк-молебне', 'Европейский суд по правам человека (ЕСПЧ) обязал Россию в течение трех месяцев выплатить более 30 тысяч евро участницам группы Pussy Riot за дело о панк-молебне в храме. Об этом говорится в судебном решении, опубликованном на сайте ЕСПЧ 17 июля.');
INSERT INTO php2.news (id, title, content) VALUES (2, 'Ким Чен Ын переоделся и устроил разнос подчиненным', 'Лидер Северной Кореи Ким Чен Ын посетил несколько предприятий и устроил разнос местным служащим. Об этом сообщает ЦТАК.
